import React, { Component } from 'react';
import './App.css';
import axios from 'axios';

const regularRequest = axios.create({
  baseURL: 'http://localhost:3500/list',
});


class App extends Component {
  state = {
    list: []
  }

  getConstellations = () => {
    const url = 'http://127.0.0.1:3500/list'
    fetch(url)
      .then(response => {
        return response.json()
      })
      .then(list => {
        console.log('list', ': ', list);
        this.setState({ list: list })
        console.log('this.state.list',': ', this.state.list);

      })
  }

  componentDidMount() {
    this.getConstellations()
  }

  handlePost() {
    regularRequest.post()
      .then(resp => console.log(resp.data));
    // fetch(url, {
    //   method: "POST", // *GET, POST, PUT, DELETE, etc.
    //   headers: {
    //       "Content-Type": "application/json; charset=utf-8",
    //       // "Content-Type": "application/x-www-form-urlencoded",
    //   },
    //   body: JSON.stringify(data), // body data type must match "Content-Type" header
    // })
    //   .then(resp => resp.json())
    //   .then(json => console.log(json));
  }

  render() {
    return (
      <div>
        <ol>
          {this.state.list.map(doc => {
            return <li> {doc.item} </li>
          })}
        </ol>

        <button onClick={this.handlePost}> Add Item </button>
      </div>
    );
  }
}

export default App;
