import React, { Component } from 'react';
import './App.css';
import axios from 'axios';

const regularRequest = axios.create({
  baseURL: 'http://localhost:3500/',
});


class App extends Component {
  state = {
    list: [], 
    input: ""
  }

  getConstellations = () => {
    const url = 'http://127.0.0.1:3500/list'
    fetch(url)
      .then(response => {
        return response.json()
      })
      .then(list => {
        console.log('list', ': ', list);
        this.setState({ list: list })

      })
  }

  componentDidMount() {
    this.getConstellations()
  }

  handlePost() {
    console.log('this.state.list', ': ', this.state.list);

    regularRequest.post('/list', {
      item: 'test'
    })
    .then(resp => console.log(resp.data))
    .catch(error => {
      console.log(error);
    });
  }

  render() {
    return (
      <div>
        <ol>
          {this.state.list.map(doc => {
            return <li> {doc.item} </li>
          })}
        </ol>

        <button onClick={this.handlePost.bind(this)}> Add Item </button>
      </div>
    );
  }
}

export default App;
